static Bool
PVRRenderDeInit(PPVRSERVICES pSrv)
{
	PVR2DCONTEXTHANDLE hPVR2DContext = pSrv->hPVR2DContext;
	int i;

	PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGB[PictOpSrc]);
	PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGB[PictOpOverReverse]);
	PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGB[PictOpIn]);
	PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGB[PictOpOut]);
	PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGB[PictOpAdd]);

	for (i = 0; i < PictOpConjointMaximum + 1; i++)
	{
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.ARGBtoARGB[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.A8toARGB[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.A8toXRGB[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.ARGBtoARGBMaskAlpha[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.ARGBtoARGBMaskFull[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGBMaskAlpha[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoARGBMaskFull[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.ARGBtoXRGBMaskAlpha[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.ARGBtoXRGBMaskFull[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoXRGBMaskAlpha[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.XRGBtoXRGBMaskFull[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.A8toARGBMaskAlpha[i]);
		PVR2DFreeUseCode(hPVR2DContext, gsUseCodeFragments.A8toXRGBMaskAlpha[i]);
	}


	return TRUE;
}

static PVR2DERROR
PVRSolid8BitInit(PPVRSERVICES pSrv)
{
	int i;
	PVR2DERROR iErr;

	for (i = 0; i < ARRAY_SIZE(pbexa_solid8bit_use); i++) {
		iErr = PVR2DLoadUseCode(pSrv->hPVR2DContext,
					pbexa_solid8bit_use[i].use,
					pbexa_solid8bit_use[i].len,
					&pbexa_solid8bit_use[i].hFragment);
		if (iErr != PVR2D_OK) {
			break;
		}
	}

	return iErr;
}

static void
PVRSolid8BitDeInit(PPVRSERVICES pSrv)
{
	int i;

	for (i = 0; i < ARRAY_SIZE(pbexa_solid8bit_use); i++) {
		if (pbexa_solid8bit_use[i].hFragment) {
			PVR2DFreeUseCode(pSrv->hPVR2DContext,
					 pbexa_solid8bit_use[i].hFragment);
			pbexa_solid8bit_use[i].hFragment = NULL;
		}
	}
}

Bool
PVRUseInit(ScreenPtr pScreen, PVRPtr pPVR)
{
	ScrnInfoPtr pScrn = xf86ScreenToScrn(pScreen);
	PVR2DERROR iErr;

	iErr = PVRSolid8BitInit(pPVR->srv);

	if (iErr != PVR2D_OK) {
		ERROR_MSG("PVRSolid8BitInit failed (%s)",
			  sgxErrorCodeToString(iErr));
		return FALSE;
	}

	iErr = PVRRenderInit(pPVR->srv);

	if (iErr != PVR2D_OK) {
		ERROR_MSG("PVRRenderInit failed (%s)",
			  sgxErrorCodeToString(iErr));
		return FALSE;
	}

	return TRUE;
}

void
PVRUseDeInit(ScreenPtr pScreen, PVRPtr pPVR)
{
	PVRRenderDeInit(pPVR->srv);
	PVRSolid8BitDeInit(pPVR->srv);
}

PVR2D_HANDLE
PVRSolid8BitGetUse(int alu)
{
	return pbexa_solid8bit_use[alu].hFragment;
}

