PVR2D_HANDLE
PVRGetUseCodeForRender(PVRRenderOp *pRender)
{
	PictFormatShort srcFormat = pRender->pSrcPicture->format;
	PicturePtr pMaskPicture = pRender->pMaskPicture;
	PictFormatShort destFormat = pRender->pDestPicture->format;
	Bool maskAlpha = FALSE;
	int op = pRender->op;

	if (pMaskPicture)
		maskAlpha = pMaskPicture->componentAlpha;

	if (pRender->srcForceAlpha) {
		switch (srcFormat) {
			case PICT_a8:
			case PICT_a8r8g8b8:
			case PICT_a8b8g8r8:
				break;
			case PICT_x8r8g8b8:
				srcFormat = PICT_a8r8g8b8;
				break;
			case PICT_x8b8g8r8:
				srcFormat = PICT_a8b8g8r8;
				break;
			default:
				ErrorF("PVRGetUseCodeForRender: Tried to use SRC alpha with format different than X8R8G8B8 with transparency. Format: 0x%x",
				       srcFormat);
				break;
		}
	}

	if (pRender->destForceAlpha) {
		switch (destFormat) {
			case PICT_a8:
			case PICT_a8r8g8b8:
			case PICT_a8b8g8r8:
				break;
			case PICT_x8r8g8b8:
				destFormat = PICT_a8r8g8b8;
				break;
			case PICT_x8b8g8r8:
				destFormat = PICT_a8b8g8r8;
				break;
			default:
				ErrorF("PVRGetUseCodeForRender: Tried to use DEST alpha with format different than X8R8G8B8 with transparency. Format: 0x%x",
				       destFormat);
		}
	}

	if (pRender->pMaskPicture) {
		switch (destFormat) {
			case PICT_a8r8g8b8:
			case PICT_a8b8g8r8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						if (maskAlpha)
							return gsUseCodeFragments.ARGBtoARGBMaskAlpha[op];
						else
							return gsUseCodeFragments.ARGBtoARGBMaskFull[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						if (maskAlpha)
							return gsUseCodeFragments.XRGBtoARGBMaskAlpha[op];
						else
							return gsUseCodeFragments.XRGBtoARGBMaskFull[op];
					case PICT_a8:
						return gsUseCodeFragments.A8toARGBMaskAlpha[op];
					default:
						break;
				}
			case PICT_x8r8g8b8:
			case PICT_x8b8g8r8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						if (maskAlpha)
							return gsUseCodeFragments.ARGBtoXRGBMaskAlpha[op];
						else
							return gsUseCodeFragments.ARGBtoXRGBMaskFull[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						if (maskAlpha)
							return gsUseCodeFragments.XRGBtoXRGBMaskAlpha[op];
						else
							return gsUseCodeFragments.XRGBtoXRGBMaskFull[op];
					case PICT_a8:
						return gsUseCodeFragments.A8toXRGBMaskAlpha[op];
					default:
						break;
				}
				break;
			case PICT_a8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						return gsUseCodeFragments.ARGBtoARGBMaskAlpha[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						return gsUseCodeFragments.XRGBtoARGBMaskAlpha[op];
					case PICT_a8:
						return gsUseCodeFragments.ARGBtoARGBMaskAlpha[op];
					default:
						break;
				}
				break;
			default:
				break;
		}
	} else {
		switch (destFormat) {
			case PICT_a8r8g8b8:
			case PICT_a8b8g8r8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						return gsUseCodeFragments.ARGBtoARGB[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						return gsUseCodeFragments.XRGBtoARGB[op];
					case PICT_a8:
						return gsUseCodeFragments.A8toARGB[op];
					default:
						break;
				}
				break;
			case PICT_x8r8g8b8:
			case PICT_x8b8g8r8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						return gsUseCodeFragments.ARGBtoXRGB[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						return gsUseCodeFragments.XRGBtoXRGB[op];
					case PICT_a8:
						return gsUseCodeFragments.A8toXRGB[op];
					default:
						break;
				}
				break;
			case PICT_a8:
				switch (srcFormat) {
					case PICT_a8r8g8b8:
					case PICT_a8b8g8r8:
						return gsUseCodeFragments.ARGBtoARGB[op];
					case PICT_x8r8g8b8:
					case PICT_x8b8g8r8:
						return gsUseCodeFragments.XRGBtoARGB[op];
					case PICT_a8:
						return gsUseCodeFragments.ARGBtoARGB[op];
					default:
						break;
				}
				break;
			default:
				break;
		}
	}

	return NULL;
}
